package org.vaadin.addons.streamingcontainer;

// TODO: Auto-generated Javadoc
/**
 * The Enum EStatus.
 */
public enum EStatus
{
    /** Bean is loaded and not modified */
    Loaded,

    /** Bean was loaded and is marked as "to update" */
    Updated,

    /** Bean is marked as "to add/insert" */
    Added,

    /** Bean is marked as "to remove/delete" */
    Removed;
}
